package while_Pattern;

public class Invertedtriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//****
		//***
		//**
		//*
		int n =4;
		int i =1;
		while(i<=n) {
			int j = 1;
			while(j<=n-i+1) {
				System.out.print("*");
				j++;
			}
			System.out.println();
			i++;
		}
	}

}
